import { useState, useEffect } from "react";
import "../css/Featured.css";
import Product from "./Product";

function Featured() {
  const [products, setProducts] = useState([]);
  const [type, setType] = useState("Vitamins");
  const [filtered, setFiltered] = useState([]);

  useEffect(() => {
    import("../database/products.json")
      .then((data) => {
        setProducts(data.products);
      })
      .catch((error) => {
        console.error("Error fetching local JSON data:", error);
      });
  }, []);

  useEffect(() => {
    const filteredProducts = products.filter((product) =>
      product.type.includes(type)
    );
    setFiltered(filteredProducts);
  }, [type, products]);

  handle

  return (
    <div className="featured-container section-padding">
      <div className="featured-title">
        <h2>Featured Products</h2>
      </div>
      <div className="product-categories">
        <button
          onClick={() => {
            setType("Vitamins");
            console.log("Selected type: Vitamins");
          }}
          className="active"
        >
          Vitamins
        </button>
        <button onClick={() => setType("Face Care")}>Face Care</button>
        <button onClick={() => setType("Body Care")}>Body Care</button>
        <button onClick={() => setType("Eye Care")}>Eye Care</button>
        <button onClick={() => setType("Health")}>Health</button>
        <button onClick={() => setType("Personal Care")}>Personal Care</button>
        <button onClick={() => setType("Muscle Care")}>Muscle Care</button>
      </div>
      <div className="product-list">
        {filtered.map((product) => (
          <Product key={product.id} product={product} />
        ))}
      </div>
    </div>
  );
}

export default Featured;
