import { useState, useEffect } from "react";
import "../css/Featured.css";
import Product from "./Product";

function Featured() {
  const [products, setProducts] = useState([]);
  const [type, setType] = useState("Vitamins");
  const [filtered, setFilter] = useState([]);

  useEffect(() => {
    import("../database/products.json")
      .then((data) => {
        setProducts(data.products);
      })
      .catch((error) => {
        console.error("Error fetching local JSON data:", error);
      });
  }, []);

  useEffect(()=>{
    setFilter([]);
    for(let i=0;i<products.length;i++)
    {
        if(products[i].type[0] === type || products[i].type[1] === type)
        { 
          setFilter(filter => [...filter, products[i]]);
        }    
    }
  },[type, products])
  const handleClick = (event) => {
    const buttonText = event.target.textContent;
    setType(buttonText);
    console.log(event);
  };

  return (
    <div className="featured-container section-padding">
      <div className="featured-title">
        <h2>Featured Products</h2>
      </div>
      <div className="product-categories">
        <button onClick={handleClick}>Vitamins</button>
        <button onClick={handleClick}>Face Care</button>
        <button onClick={handleClick}>Body Care</button>
        <button onClick={handleClick}>Eye Care</button>
        <button onClick={handleClick}>Health</button>
        <button onClick={handleClick}>Personal Care</button>
        <button onClick={handleClick}>Muscle Care</button>
      </div>
      <div className="product-list">
        {filtered.map((product) => (
          <Product key={product.id} product={product} />
        ))}
      </div>
    </div>
  );
}

export default Featured;
