import { useState, useEffect } from "react";
import "../css/Featured.css";
import ProductCard from "./ProductCard";


function Featured() {
  const [products, setProducts] = useState([]);
  const [type , setType] = useState("Vitamins")

  useEffect(() => {
    import('../database/products.json')
      .then(data => {
        setProducts(data.products);
      })
      .catch(error => {
        console.error('Error fetching local JSON data:', error);
      });
  }, []);

  // console.log(products);
  function settingtype(type){
    setType(type);
  }
  console.log(type)

  return (
    <div className="featured-container section-padding">
      <div className="featured-title">
        <h2>Featured Products</h2>
      </div>
      <div className="product-categories">
        <button className="active">Vitamins</button>
        <button onClick={()=>settingtype("Face Care")}>Face Care</button>
        <button onClick={()=>settingtype("Body Care")}>Body Care</button>
        <button onClick={()=>settingtype("Medical Equipment")}>Medical Equiment</button>
        <button onClick={()=>settingtype("Health")}>Health</button>
        <button onClick={()=>settingtype("Personal Care")}>Personal Care</button>
        <button onClick={()=>settingtype("Muscle Care")}>Muscle Care</button>
      </div>
      <div className="products-filtered"> 
          {products.map((product)=>(
            <ProductCard key={product.id} props={product}/>
          ))
            
          }
        
      </div>
     
    </div>
  );
}

export default Featured;
